import React from 'react';
import { Routes, Route } from 'react-router-dom';
import { useSelector } from 'react-redux';
import Navbar from './components/Navbar';
import './App.css';

// Pages
const Home = () => <div className="page">Page d'accueil</div>;
const Login = () => <div className="page">Page de connexion</div>;
const Register = () => <div className="page">Page d'inscription</div>;
const Courses = () => <div className="page">Liste des cours</div>;
const Profile = () => <div className="page">Profil utilisateur</div>;
const InstructorDashboard = () => <div className="page">Dashboard Instructeur</div>;
const AdminDashboard = () => <div className="page">Dashboard Admin</div>;
const NotFound = () => <div className="page">Page non trouvée</div>;

function App() {
  const { isAuthenticated, user } = useSelector((state) => state.auth);

  return (
    <div className="App">
      <Navbar />
      <main className="main-content">
        <Routes>
          {/* Routes publiques */}
          <Route path="/" element={<Home />} />
          <Route path="/courses" element={<Courses />} />
          
          {/* Routes d'authentification */}
          {!isAuthenticated && (
            <>
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />
            </>
          )}

          {/* Routes protégées */}
          {isAuthenticated && (
            <>
              <Route path="/profile" element={<Profile />} />
              {user?.role === 'instructor' && (
                <Route path="/instructor/dashboard" element={<InstructorDashboard />} />
              )}
              {user?.role === 'admin' && (
                <Route path="/admin/dashboard" element={<AdminDashboard />} />
              )}
            </>
          )}

          {/* Route 404 */}
          <Route path="*" element={<NotFound />} />
        </Routes>
      </main>
    </div>
  );
}

export default App;
